//-------------------------------------------------------
//  Generator: App
//-------------------------------------------------------

var _generator = require('yeoman-generator');
var _inquirer = require('inquirer');
var _chalk = require('chalk');

var _PromptHelper = require('../../lib/shared/PromptHelper');

//-------------------------
//  Private
//-------------------------

var _promptMainObj = _PromptHelper.getListObj('Type', ['Components', '|', 'Exit']);

//-------------------------
//  Yeoman Base Extension
//-------------------------

var baseExtension = {

  _promptType: function () {
    return this.prompt(_promptMainObj).then(function (answers) {
      // compose with the user's selected
      switch(answers.type) {
        case 'Components':
          this.composeWith('jaywing-atomic:components');
          break;
        case 'Exit':
          break;
      }
    }.bind(this));
  },

  _promptStart: function () {

    this._promptType();

  },

  initializing: function () {

    this.log(' -------------------\\/-------------------');
    this.log(' |         JAYWING ATOMIC GENERATOR     |');
    this.log(' -------------------/\\-------------------');

  },

  prompting: function () {
    this._promptStart();
  }

}

module.exports = _generator.Base.extend(baseExtension);