//-------------------------------------------------------
//  Library: Data Helper
//-------------------------------------------------------

var _ = require('lodash');

var _config = require('../../../components/config');

//-------------------------
//  Private
//-------------------------

//-------------------------
//  Public
//-------------------------

var self = module.exports = {

  // Data

  extractDataCategoryNames: function (categoriesData) {
    var names = [];
    for(var i = 0; i < categoriesData.length; i++) {
      names.push(categoriesData[i].name);
    }
    return names;
  },

  extractDataSubCategoryNames: function (categoryData) {
    var names = [];
    var subCategories = categoryData.categories;
    for (var i = 0; i < subCategories.length; i++) {
      var subCategory = subCategories[i];
      var name = subCategory.name;
      names.push(name);
    }
    return names;
  },

  extractDataSizeNames: function (sizesData) {
    var names = [];
    for(var i = 0; i < sizesData.length; i++) {
      names.push(sizesData[i].name);
    }
    return names;
  },

  getDataSummaryObj: function (categoryName, subCategoryName, name, description, sizeNames, moduleClassName) {

    var obj = {};

    obj.categoryName = categoryName;
    obj.subCategoryName = subCategoryName;
    obj.name = name;
    obj.description = description;
    obj.cssClassName = this.getCssClassName(name);
    obj.sizeNames = sizeNames.length > 0 ? sizeNames : 'None';
    obj.sizeCssClassNames = sizeNames.length > 0 ? this.getSizeCssClassNames(name, sizeNames) : 'N/A';
    obj.moduleClassName = moduleClassName ? moduleClassName : 'None';

    return obj;

  },

  getDataObj: function (categoryName, subCategoryName, name, description, sizeNames, moduleClassName) {

    var obj = {};

    // basic
    obj.categoryName = categoryName;
    obj.subCategoryName = subCategoryName;
    obj.name = name;
    obj.description = description;
    obj.cssClassName = _helper.getCssClassName(name);
    obj.sizeNames = sizeNames;
    obj.sizeCssClassNames = sizeNames.length > 0 ? _helper.getSizeCssClassNames(name, sizeNames) : null;
    obj.fullCssClassName = _helper.getFullCssClassName(name, sizeNames, []);
    obj.moduleClassName = moduleClassName;

    // sass
    obj.sassVarPrefix = _helper.getSassVarPrefix(name);
    obj.sassTempColor = _config.templateData.components.sass.tempColor;
    obj.sassTempBackground = _config.templateData.components.sass.tempBackground;
    obj.sassTempPadding =_config.templateData.components.sass.tempPadding;

    // docs
    obj.docsPageId = _helper.getDocsPageId(name);
    obj.dataModuleAttr = obj.moduleClassName ? ' data-module=\"' + obj.moduleClassName + '\"' : '';
    obj.exampleHtml = '<div class=\"' + obj.fullCssClassName + '\"' + obj.dataModuleAttr + '>\n\n\t\t\t<h4>' + _name + '</h4>\n\n\t\t</div>';
    obj.codeHtml = '<div class=\"' + obj.fullCssClassName + '\"' + obj.dataModuleAttr + '>\n\n\t\t\t\t\t<h4>' + _name + '</h4>\n\n\t\t\t\t</div>';

    return obj;

  }

};