var self = module.exports = {

  getDocsDataId: function (name) {
    var result = name.replace(/\s+/g, '-').toLowerCase();
    return result;
  },

  extractDocsDataPage: function (docsData, categoryName, subCategoryName) {

    var page = null;

    var pages = docsData.docs_pages;
    for (var p = 0; p < pages.length; p++) {
      var category = pages[i].category;
      var section = pages[i].section;
      if(category == subCategoryName && section == categoryName.toLowerCase()){
        page = pages[i];
      }
    }

    return page;

  },

  createDocsDataPageItem: function (name) {

    var obj = {};

    obj.id = this.getDocsDataId(name);
    obj.title = name;
    obj.section = "components";
    obj.ready = true;

    return obj;

  }

};