//-------------------------------------------------------
//  Library: Data Syncer
//-------------------------------------------------------

var _fs = require('fs');

var _config = require('../../config');

//-------------------------
//  Private
//-------------------------

var _generator;

//-------------------------
//  Public
//-------------------------

var self = module.exports = {

  init: function (generator) {

    _generator = generator;

  },

  docsDataExists: function () {

    var path = _config.docs.files.global;
    var exists = _fs.existsSync(path);

    return exists;

  },

  addDocsData: function (data) {

    var path = _config.docs.files.global;
    var data = {};
    _generator.fs.copyTpl(_generator.templatePath('data.json'), _generator.destinationPath(path), data);

  }

};