//-------------------------------------------------------
//  Library: Data Helper
//-------------------------------------------------------

var _ = require('lodash');

var _config = require('../../data/config');

//-------------------------
//  Private
//-------------------------

//-------------------------
//  Public
//-------------------------

var self = module.exports = {

  getCategoryNamesFromCategoryData: function (categories) {
    var names = [];
    for(var i = 0; i < categories.length; i++) {
      names.push(categories[i].name);
    }
    return names;
  },

  getNameFromSassFolderName: function (folderName) {
    var strs = folderName.toLowerCase().match(/[^\s-]+/g);
    var result = '';
    for(var i = 0; i < strs.length; i++) {
      result += _.upperFirst(strs[i]) + ' ';
    }
    result = result.trim();
    return result;
  },

  getCategorySassPathFromCategoryName: function (name) {
    var path = _config.components.paths.sass + name.replace(/\s+/g, '-').toLowerCase();;
    return path;
  }

};