//-------------------------

function _setTemplateData(categoryName, subCategoryName, name, description, sizeNames, moduleClassName) {

  var obj = {};

  // basic
  obj.categoryName = categoryName;
  obj.subCategoryName = subCategoryName;
  obj.name = name;
  obj.description = description;
  obj.cssClassName = _helper.getCssClassName(name);
  obj.sizeNames = sizeNames;
  obj.sizeCssClassNames = sizeNames.length > 0 ? _helper.getSizeCssClassNames(name, sizeNames) : null;
  obj.fullCssClassName = _helper.getFullCssClassName(name, sizeNames, []);
  obj.moduleClassName = moduleClassName;

  // sass
  obj.sassVarPrefix = _helper.getSassVarPrefix(name);
  obj.sassTempColor = _config.templateData.components.sass.tempColor;
  obj.sassTempBackground = _config.templateData.components.sass.tempBackground;
  obj.sassTempPadding =_config.templateData.components.sass.tempPadding;

  // docs
  obj.docsPageId = _helper.getDocsPageId(name);
  obj.dataModuleAttr = obj.moduleClassName ? ' data-module=\"' + obj.moduleClassName + '\"' : '';
  obj.exampleHtml = '<div class=\"' + obj.fullCssClassName + '\"' + obj.dataModuleAttr + '>\n\n\t\t\t<h4>' + _name + '</h4>\n\n\t\t</div>';
  obj.codeHtml = '<div class=\"' + obj.fullCssClassName + '\"' + obj.dataModuleAttr + '>\n\n\t\t\t\t\t<h4>' + _name + '</h4>\n\n\t\t\t\t</div>';

  _templateData = obj;

}

