//-------------------------------------------------------
//  Library: Data Manager
//-------------------------------------------------------

var _miner = require('../../lib/components/miner');
var _helper = require('../../lib/components/helper');

//-------------------------
//  Private
//-------------------------

var _data = {};

//-------------------------
//  Public
//-------------------------

var self = module.exports = {

  initData: function () {

    // init
    _data.componentData = {};

    // defaults
    var defaults = {name:'Defaults'};
    var objects = {name:'Objects'};
    var elements = {name:'Elements'};
    var navigation = {name:'Navigation'};
    var common = {name:'Common'};

    _data.componentData.categories = [defaults, objects, elements, navigation, common];

  },

  getCategoryNames: function () {
    var names = _helper.getCategoryNamesFromCategoryData(_data.componentData.categories);
    return names;
  },

  getNames: function (category) {
    var names = _miner.getNamesFromSassFolders(category);
    return names;
  },

  getModuleNames: function () {
    var names = _miner.getModuleNamesFromModuleFiles();
    return names;
  }

};