//-------------------------------------------------------
//  Library: Data Syncer
//-------------------------------------------------------

var _fs = require('fs');

var _config = require('../../data/config');
var _helper = require('../../lib/components/helper');

//-------------------------
//  Private
//-------------------------

//-------------------------
//  Public
//-------------------------

var self = module.exports = {

  getNamesFromSassFolders: function (category) {

    var names = [];

    var path = _helper.getCategorySassPathFromCategoryName(category);
    var exists = _fs.existsSync(path);
    if(exists) {
      var readList = _fs.readdirSync(path);
      for(var i = 0; i < readList.length; i++) {
        var item = readList[i];
        var name = _helper.getNameFromSassFolderName(item);
        names.push(name);
      }
    }

    return names;

  },

  getModuleNamesFromModuleFiles: function (category) {

    var names = [];

    var path = _config.components.paths.modules;
    var exists = _fs.existsSync(path);
    if(exists) {
      var readList = _fs.readdirSync(path);
      for(var i = 0; i < readList.length; i++) {
        var item = readList[i].split('.');
        var name = item[0];
        names.push(name);
      }
    }

    return names;

  }

};