//-------------------------------------------------------
//  Library: Data Syncer
//-------------------------------------------------------

var _fs = require('fs');

var _helper = require('../../lib/components/helper');

//-------------------------
//  Private
//-------------------------

//-------------------------
//  Public
//-------------------------

var self = module.exports = {

  getNamesFromSassFolders: function (category) {

    var names = [];
    var path = _helper.getCategorySassPathFromCategoryName(category);
    var exists = _fs.existsSync(path);
    if(exists) {
      var folderNames = _fs.readdirSync(path);
      for(var i = 0; i < folderNames.length; i++) {
        var folderName = folderNames[i];
        var name = _helper.getNameFromSassFolderName(folderName);
        names.push(name);
      }
    }

    return names;

  }

};