//-------------------------------------------------------
//  Library: Data Manager
//-------------------------------------------------------

//-------------------------
//  Import
//-------------------------

var _config = require('../data/config');
var _dataSyncer = require('../lib/dataSyncer');

//-------------------------
//  Private Vars
//-------------------------

var _data = {};

//-------------------------
//  Private Funcs
//-------------------------

function _initComponentData() {

  // init
  _data.componentData = {};

  // defaults
  var defaults = {name:'Defaults'};
  var objects = {name:'Objects'};
  var elements = {name:'Elements'};
  var navigation = {name:'Navigation'};
  var common = {name:'Common'};
  _data.componentData.categories = [defaults, objects, elements, navigation, common];

}

//-------------------------
//  Private Getters
//-------------------------

function _getComponentCategoryNames() {
  var names = [];
  for(var i = 0; i < _data.componentData.categories.length; i++) {
    names.push(_data.componentData.categories[i].name);
  }
  return names;
}

//-------------------------
//  Private Setters
//-------------------------



//-------------------------
//  Public
//-------------------------

var self = module.exports = {

  initComponentData: function () {
    _initComponentData();
  },

  getComponentCategoryNames: function () {
    return _getComponentCategoryNames();
  }

};