//-------------------------------------------------------
//  Library: Data Helper
//-------------------------------------------------------

var _inquirer = require('inquirer');
var _ = require('lodash');

//-------------------------
//  Private
//-------------------------

//-------------------------
//  Public
//-------------------------

var self = module.exports = {

  getInputObj: function (message, defaultInput) {

    var obj = {};

    obj.type = 'input';
    obj.name = _.camelCase(message);
    obj.message = message + ':';
    if(defaultInput) obj.default = defaultInput;

    return obj;

  },

  getListObj: function (message, choices) {

    var obj = {};

    obj.type = 'list';
    obj.name = _.camelCase(message);
    obj.message = message + ':';

    if(choices) {
      var choiceList = [];
      for(var i = 0; i < choices.length; i++) {
        var choice = choices[i];
        if(choice == '|') choice = new _inquirer.Separator();
        choiceList.push(choice);
      }
      obj.choices = choiceList;
    }

    return obj;

  },

  getCheckboxesObj: function (message, choices) {

    var obj = {};

    obj.type = 'checkbox';
    obj.name = _.camelCase(message);
    obj.message = message + ':';
    if(choices) obj.choices = choices;

    return obj;

  },

  getCheckboxChoices: function (choices, checkAll) {
    var checkboxes = [];
    for(var i = 0; i < choices.length; i++) {
      var checkbox = {};
      checkbox.name = choices[i];
      checkbox.value = choices[i];
      checkbox.checked = checkAll ? true : false;
      checkboxes.push(checkbox);
    }
    return checkboxes;
  }

};